import { settingsObjectsClient } from "@dynatrace-sdk/client-classic-environment-v2";

/*
* This will run JavaScript in the DYNATRACE
* serverless environment.
* To generate variable options return string array.
*/
export default async function () {
    const data = await settingsObjectsClient.getEffectiveSettingsValues({
    schemaIds: "builtin:ownership.teams",
    scope: "environment",
    fields: "objectId,value",
    pageSize: 50,
  });

  var teamNames = new Array(data.totalCount+1)
  teamNames[0] = "No Team"
  for (var teamIx=0;teamIx<data.totalCount;teamIx++) {
    teamNames[teamIx+1]=data.items[teamIx].value.name;
  }

  return teamNames;
}

