import { queryExecutionClient } from '@dynatrace-sdk/client-query';

async function getAllEntities() {
  const timeout = 60;
  const query = 'fetch dt.entity.' + $entitytype + ' | fieldsAdd hasOwnerTags=(isNotNull(tags) and indexOf(toString(tags),"owner")>=0),tags' 

  const response = await queryExecutionClient.queryExecute({ body: { query, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout  } });
  return response.result.records;
}

async function getAllEntitiesForTeam(team) {
  const timeout = 60;
  const query = 'fetch dt.entity.' + $entitytype + ' | filter (isNotNull(tags) and indexOf(toString(tags),"owner:' + team + '")>=0) | fieldsAdd tags' 
  console.log(query)

  const response = await queryExecutionClient.queryExecute({ body: { query, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout  } });
  return response.result.records;
}

async function getAllEntitiesWitoutTeam(team) {
  const timeout = 60;
  const query = 'fetch dt.entity.' + $entitytype + ' | filter (isNull(tags) or indexOf(toString(tags),"owner:")==1)' 

  const response = await queryExecutionClient.queryExecute({ body: { query, requestTimeoutMilliseconds: timeout * 1000, fetchTimeoutSeconds: timeout  } });
  return response.result.records;
}

/**
 * entities: array of entities with id and array of tags
 * team: name of team that we find the match for
 */
async function getEntitiesOwnedByTeam(entities,team) {

  console.log("Searching for entities owned by: " + team)

  var ownedEntities = new Array();

  // iterate through all entities
  for (var entityIx=0;entityIx<entities.length;entityIx++) {
    // iterate through all tags and see if we find a matching team
    var entity=entities[entityIx]
    for (var tagIx=0;tagIx<entity.tags.length;tagIx++) {
      var tagToCheck = entity.tags[tagIx]
      console.log("Checking tag: " + tagToCheck)
      if ((tagToCheck.indexOf("dt.owner") == 0) && (tagToCheck.indexOf(team) > 0)) {
        console.log("Found tag: " + tagToCheck)
        ownedEntities.push(entity)
      }
    }
  }

  return ownedEntities;
}

export default async function () { 
  if ($team == "No Team" || $team == null) {
    console.log("No Team: " + $team)
    return await getAllEntitiesWitoutTeam()
  } else {
    console.log("Team: " + $team)
    return await getAllEntitiesForTeam($team)
  }
}
