27.query:
fetch logs
| filter event.type == "K8S" AND matchesValue(dt.kubernetes.cluster.name,$cluster_name)
| summarize events = count(), by: {bin(timestamp,5m), alias: timestamp, loglevel}

31.query:
fetch logs
| filter event.type == "K8S" AND matchesValue(dt.kubernetes.cluster.name,$cluster_name)
| summarize events = count(), by: {dt.event.group_label, alias: category}
