0.query:
fetch logs
| filter matchesValue(k8s.namespace.name, "backstage")
| parse content, "JSON{STRING:entity,STRING:level,STRING:location,STRING:message}(flat=true)"
| filter matchesValue(level, "warn") and isNotNull(entity)
| fields 
`Timestamp` = timestamp, `Name` = substring(entity, from: indexOf(entity, "/") + 1),
`Namespace` = substring(entity, from: indexOf(entity, ":") + 1, to: indexOf(entity, "/")),
`Type` = substring(entity, to: indexOf(entity, ":")), `Location` = substring(location, from: indexOf(location, ":") + 1),
`Error` = if(
  contains(message, "NotFoundError") and contains(message, "PlaceholderProcessor"), "External URL: Not found", 
  else: if(contains(message, "NotFoundError"), "Entity not found",
  else: if(contains(message, "Unauthorized"), "External URL: Unauthorized",
  else: if(contains(message, "NotAllowedError"), "External URL: Not allowed",
  else: if(contains(message, "required property"), "Required property missing",
  else: if(contains(message, "Policy check failed") or contains(message, "TypeError"), "Invalid syntax",
  else: if(contains(message, "Entity reference"), "Invalid reference",
  else: "Other"
  ))))))
),
`Message` = message
| filter in(Error, array($EntityError))
| sort Timestamp desc
// | filter Type == "resource"

1.query:
fetch logs
| filter matchesPhrase(k8s.namespace.name, "backstage")
| parse content, "JSON{STRING:entity,STRING:level,STRING:message}(flat=true)"
| filter (matchesValue(level, "warn") or matchesValue(level, "error")) and isNotNull(entity)
| fields 
error = if(
  contains(message, "NotFoundError") and contains(message, "PlaceholderProcessor"), "External URL: Not found", 
  else: if(contains(message, "NotFoundError"), "Entity not found",
  else: if(contains(message, "Unauthorized"), "External URL: Unauthorized",
  else: if(contains(message, "NotAllowedError"), "External URL: Not allowed",
  else: if(contains(message, "required property"), "Required property missing",
  else: if(contains(message, "Policy check failed") or contains(message, "TypeError"), "Invalid syntax",
  else: if(contains(message, "Entity reference"), "Invalid reference",
  else: "Other"
  ))))))
),
`Message` = message
| filter in(error, array($EntityError))
| summarize count(), alias: Error, by: { error }
| sort Error desc
