| filter dt.system.bucket=="default_security_events"
| filter event.provider=="Dynatrace"
| filter event.type=="VULNERABILITY_STATE_REPORT_EVENT"
| filter event.level=="ENTITY"
// filter for the latest snapshot per entity
| dedup {vulnerability.display_id, affected_entity.id}, sort:{timestamp desc}
| lookup [
    fetch dt.entity.process_group_instance, from:now() - 2h
    | fields entity.name, releasesVersion, process_group = instance_of[dt.entity.process_group], releasesBuildVersion, releasesProduct, releasesStage
    | filter isNotNull(releasesVersion)
    | fieldsAdd releaseVersion = parse(releasesVersion,""" "ReleaseVersionInfo{version='"LD:version"'"LD """)
    | summarize entity.name = takeAny(entity.name), by:{
                   process_group,
                   releaseVersion,
                   releasesBuildVersion,
                   releasesProduct,
                   releasesStage
                 }
    | fields entity.name, entity.id=process_group, Version=releaseVersion, Build=releasesBuildVersion, Stage=releasesStage, Product=releasesProduct
], sourceField:affected_entity.id, lookupField:entity.id, fields:{entity.name,entity.id,Version,Build,Stage,Product}
| fieldsAdd Product=coalesce(Product,"-"), Stage=coalesce(Stage,"-")
// now summarize on the vulnerability level
| sort {timestamp, direction:"descending"}
| summarize
{
  vulnerability.stack=takeAny(vulnerability.stack),
  vulnerability.type=takeAny(vulnerability.type),
