38.query:
fetch dt.entity.host
| fieldsAdd entity.name, entity.detected_name, ipAddress
| expand ipAddress
| join [
   fetch events
  | filter dt.system.bucket == "default_security_custom_events"
  | filter event.kind == "SECURITY_EVENT"
  | filter object.type == "HOST"
  // filter for events fitting the SD requirements
  | filterOut isNull(finding.id) OR isNull(object.id) OR isNull(dt.security.risk.score)
  | filter in(event.provider, $Product)
          AND (in(event.type, $FindingType))
          AND (in(dt.security.risk.level, $RiskLevel))
  | dedup {object.id, finding.id}, sort: {timestamp desc}
  | expand host.ip
  | sort timestamp desc
  | summarize {
      timestamp=takeLast(timestamp),
      dt.security.risk.level=takeLast(dt.security.risk.level)
    }, by:{object.name=coalesce(object.name,object.id), host.ip, event.provider, finding.id}
], kind:leftOuter, on:{left[ipAddress]==right[host.ip]}
| filter isNotNull(right.host.ip)
| summarize {
  `Last finding`=takeMax(right.timestamp),
  `Host entity`=takeLast(entity.name),
  IP=collectDistinct(ipAddress),
  Critical=countIf(right.dt.security.risk.level=="CRITICAL"),
  High=countIf(right.dt.security.risk.level=="HIGH"),
  Medium=countIf(right.dt.security.risk.level=="MEDIUM")
}, by:{dt.source_entity=id, `Object name`=right.object.name}
| sort {Critical,direction:"descending"}, {High,direction:"descending"}, {Medium,direction:"descending"}
| limit 10

