41.query:
fetch dt.entity.container_group_instance
| fieldsAdd entity.name, containerImageDigest, containerImageName, workloadName, workload.id=belongs_to[dt.entity.cloud_application]
| join [
   fetch events
  | filter dt.system.bucket == "default_security_custom_events"
  | filter event.kind == "SECURITY_EVENT"
  // filter for events fitting the SD requirements
  | filterOut isNull(event.type) or isNull(object.id)
  // filter with the dashboard variables
  | filter (in(event.provider, $Product))
       AND (in(object.type, $AffectedObjectType))
       AND (in(event.type,$EventType))
  | filterOut isNull(container_image.digest)
  | summarize {
      Findings=countIf(in(event.type,{"VULNERABILITY_FINDING","DETECTION_FINDING","COMPLIANCE_FINDING"})),
      Scans=countIf(in(event.type,{"VULNERABILITY_SCAN","COMPLIANCE_SCAN"}))
    }, by:{container_image.digest, event.provider}
], kind:leftOuter, on:{left[containerImageDigest]==right[container_image.digest]}
| fieldsAdd covered=if(isNotNull(right.container_image.digest), "Covered", else:"Not covered")
| summarize {
  Containers=countDistinctExact(workload.id)
}, by:{covered}

